// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

contract BudgetAllocation {
    struct Budget {
        uint256 income;
        uint256 needs;
        uint256 wants;
        uint256 savings;
        uint256 emergencyFund;
        bool strictMode;
    }

    mapping(address => Budget) public userBudgets;

    event BudgetSet(
        address indexed user,
        uint256 income,
        uint256 needs,
        uint256 wants,
        uint256 savings,
        uint256 emergencyFund,
        bool strictMode
    );

    event BudgetUpdated(address indexed user);

    modifier validAllocation(uint256 needs, uint256 wants, uint256 savings, uint256 emergencyFund, uint256 income) {
        require(needs + wants + savings + emergencyFund <= income, "Total allocation exceeds income");
        _;
    }

    function setBudget(
        uint256 income,
        uint256 needs,
        uint256 wants,
        uint256 savings,
        uint256 emergencyFund,
        bool strictMode
    ) external validAllocation(needs, wants, savings, emergencyFund, income) {
        userBudgets[msg.sender] = Budget(
            income,
            needs,
            wants,
            savings,
            emergencyFund,
            strictMode
        );

        emit BudgetSet(msg.sender, income, needs, wants, savings, emergencyFund, strictMode);
    }

    function updateEmergencyFund(uint256 newAmount) external {
        require(userBudgets[msg.sender].income > 0, "Budget not set");
        uint256 total = userBudgets[msg.sender].needs + userBudgets[msg.sender].wants + userBudgets[msg.sender].savings + newAmount;
        require(total <= userBudgets[msg.sender].income, "Exceeds income");

        userBudgets[msg.sender].emergencyFund = newAmount;

        emit BudgetUpdated(msg.sender);
    }

    function toggleStrictMode(bool newState) external {
        require(userBudgets[msg.sender].income > 0, "Budget not set");

        userBudgets[msg.sender].strictMode = newState;

        emit BudgetUpdated(msg.sender);
    }

    function getBudget(address user) external view returns (Budget memory) {
        return userBudgets[user];
    }
}

